import React, { Component } from 'react';
   const Weather = (props) => {
    return(<div>
        { props.city && this.props.country && <p>Location: { this.props.city }, {this.props.country}</p>}
        { props.temperature && <p>Temperature: {this.props.temperature}</p> }
        { props.humidity &&  <p>Humidity: {this.props.humidity}</p> }
        { props.description &&  <p>Description: {this.props.description}</p> }
        { props.error && <p> {this.props.error} </p>}
       </div>)
   }
   export default Weather;
