import React from "react";      //import react object from react package that live in package json
import './App.css';
import Title from './components/Title';
import Form from './components/Form';
import Weather from './components/Weather';
import { async } from "q";


const API_KEY = "84717655ce7fa2fced83edfe8aab43b4";


class App extends React.Component { 

getWeather = async(e) => {
  e.preventDefault()
  const city = e.target.elements.name.city.value;
  const country = e.target.elements.name.country.value;
  const api_call = await fetch(`https://api.openweathermap.org/data/2.5/weather?q=${city},uk&appid=${API_KEY}`)
  const data = await api_call.json();
  console.log(data)
}


render(){
  return(<div>
   <Title/>
   <Form getWeather={this.getWeather}/>
   <Weather/>
  </div>)                   

}
}


export default App;
